import React, { useState } from 'react';

// This is a functional component named TextForm
export default function TextForm(props) {

    // Function to handle the button click event for converting text to uppercase
    const handleUpClick = () => {
        console.log("Uppercase button clicked");
        // Convert the current text to uppercase
        let newText = text.toUpperCase();
        // Update the state with the new uppercase text
        setText(newText);
    }

    const handleLoClick = () => {
        console.log("Lowercase button clicked");
        // Convert the current text to lowercase
        let newText = text.toLowerCase();
        // Update the state with the new lowecase text
        setText(newText);


    }

    const handleCapClick = () => {
        console.log("Capitialied first letter of each word");
        // Convert the current text to capitalize the first letter of each word
        let words = text.split(" "); //split text into individual words
        let capitalizedWords = words.map(word =>{
            return word.charAt(0).toUpperCase() + word.slice(1);//capitialize the first letter of each word
        });
        let newText = capitalizedWords.join(" ");//join back ords into single string
        // Update the state with the new text
        setText(newText);
        }

       


    const handleClearClick = () => {
        let newText = ('');
        // Update the state with the new lowecase text
        setText(newText);


    }


    // Function to handle the change event when the user types in the textarea
    const handleOnChange = (event) => {
        console.log("Text changed");
        // Update the state with the new text value from the textarea
        setText(event.target.value);
    }

    // useState hook to manage the state of the text. 
    // 'text' is the current state, and 'setText' is the function to update the state.
    // The initial state is set to 'enter your text'.
    const [text, setText] = useState('');

    return (
        <>
            <div className="container py-5">
                {/* Display the heading passed as a prop and the current text */}
                <div className="mb-3">
                    <h1 className="text-center text-white my-3">{props.heading}</h1>
                    {/* Textarea for user input. The value is controlled by the state. */}
                    <textarea
                        className="form-control my-2"
                        id="myBox"
                        rows="10"
                        value={text}
                        onChange={handleOnChange}
                        style={{ backgroundColor: '#f0f0f0', padding: '10px', borderRadius: '10px' }}>
                    </textarea>
                </div>
                {/* Button to convert the text to uppercase */}
                <div className="d-flex justify-content-center py-2 pb-0">
                    <button className="btn btn-primary mx-3" onClick={handleUpClick}>
                        Convert to Uppercase
                    </button>
                    <button className="btn btn-primary mx-3" onClick={handleLoClick}>
                        Convert to Lowercase
                    </button>
                    <button className="btn btn-primary mx-3" onClick={handleCapClick}>
                        Capitalize First Letter
                    </button>
                    <button className="btn btn-primary mx-3" onClick={handleCapClick}>
                        Find and Replace
                    </button>
                    <button className="btn btn-primary mx-3" onClick={handleClearClick}>
                        Clear Text
                    </button>

                </div>
                <div className="find-and-replace">
                    <input type="text"  placeholder="Find" value={find} onChange={handleFindChange} C />
                </div>

            </div>
            <div className="container my-3 text-white ">
                <div className="preview " >
                    <h1>Preview:</h1>
                    <p>{text}</p>
                </div>
                <div className="count">
                    <h1>Your Text Summary</h1>
                    <p>{text.split(" ").length} Words {text.length} Characters</p>
                    <p>{0.008 * text.split(" ").length} Minutes Read</p>
                </div>
            </div>

        </>
    );
}
